import styled from "styled-components";

export const Geral = styled.div`
  background-color:#00a21b;
  margin: 0;
  padding: 0;
  width: 100vw;
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`



export const Button = styled.button`
  background-color: #00cd74;
  color: #ffffff;
  border: none;
  height: 40px;
  width: 85%;
  border-radius: 5px;
`;
